import {useRef, useState, useContext} from "react";
import {Modal} from "../../Components/Modal";
import axios from "axios";
import AppContext from "../../AppContext";


export function ItemS({item, infoArr, }){

    const [showModal, setShowModal] = useState(false)
    const meal = useRef(null);
    const {favorites, setFavorites} = useContext(AppContext)
    let typesLength= 0
    if(item.type){
        item.type.map(type=> typesLength += type.typeProducts.length)
    }


    const clickHandler= async (item)=>{
        meal.current = {...item, "isFavorite": !item.isFavorite, "logo": infoArr[0], "name":infoArr[1]}
        const newFav= favorites.find(el=> el.id === item.id);
        if(newFav)
        {
            await axios.delete("http://localhost:3000/favorites/"+ newFav.id)
            setFavorites((prev)=> prev.filter(meal => meal !== item.id))
        }else {
           const {data} = await axios.post("http://localhost:3000/favorites", meal.current)
            setFavorites(prev=>[...prev, data])
        }


    }


    return <div className={'rounded-xl  border border-gray-100 cursor-pointer forShadow w-[250px] mb-4'}>
        {showModal && <Modal setShowModal={setShowModal} item={item} infoArr={infoArr}/>}

        <img src={item.image} alt={item.title} className={'rounded-t-xl h-[250px] object-cover'}
             onClick={()=>setShowModal(true)}/>
        <div className={'m-1 ml-2 mr-2'}>
            <div className={'font-semibold text-black/80 text-sm'}  onClick={()=>setShowModal(true)}>
                {item.title.length > 19 ? item.title.slice(0,19) +" ...": item.title}
            </div>
            {item.type? <div className={'text-xs text-black/60 mb-2'}>{typesLength} տեսակ</div>:<div className={'h-6'}></div>}
            <div className={'flex justify-between '}>
                <span className={'font-semibold text-black/80'}  onClick={()=>setShowModal(true)}>{item.price}֏</span>
                <span>
                    <i className="fa-regular fa-heart text-red-600 mr-2.5 text-xl"
                       onClick={()=>clickHandler(item)}></i>
                    <i className="fa-solid fa-cart-plus text-red-600 text-[18px]"></i>
                </span>
            </div>
        </div>
    </div>
}
