import {RestaurantMealItem} from "./RestaurantMealItem";
import {useEffect, useRef, useState} from "react";

export function RestaurantMealList({meals}){

    let start = 0;
    let end = 4;

    const [visibleMeals, setVisibleMeals] = useState(meals.slice(start,end))


    const observer = new IntersectionObserver(
        entries => {
            entries.forEach(entry=>{
                if(entry.isIntersecting) observer.unobserve((entry.target))
            })
        },{
            threshold: 0.1
        }
    )


    const lastMealObserver = new IntersectionObserver(entries => {
        const lastMeal = entries[0]
        if(!lastMeal.isIntersecting) return
        loadNewMeals()
        lastMealObserver.unobserve(lastMeal.target)
        lastMealObserver.observe(visibleMeals.at(-2))
    },{
        rootMargin: '20px 0px 0px 0px'
    })

    lastMealObserver.observe(visibleMeals.at(-2))
    visibleMeals.forEach(meal=>{
        observer.observe(meal)
    })


    const loadNewMeals=()=>{
        start += 3;
        end += 3;
        const addNewMeals = meals.slice(start,end)
        setVisibleMeals(prevMeals=>[...prevMeals, ...addNewMeals])
    }

    return <div>
        {
            visibleMeals.map((el , index)=><RestaurantMealItem key={index} item={el}/>)
        }
    </div>
}
