async function getAllShop(){
    const response = await  axios.get('http://localhost:3000/shop')
    return response.data
}

async function getAllRestaurants(name, query, endpoint ='http://localhost:3000/restaurants'){
    try{
        query ? (query = `?${query}`) :(query='');
        name ? (name = `${name}`) :(name='');

        const response = await  axios.get(`${endpoint}${name}${query}`)
        console.log(response)
        if(!response.statusText == "OK") throw new Error((response.statusText))

        return response.data

    }catch (err){
        console.log(err || err)
    }

}

