import {useEffect, useState} from "react";
import {Modal} from "../../Components/Modal";
import {useParams} from "react-router-dom";
import axios from "axios";


export function ItemS({item, infoArr}){

    const [showModal, setShowModal] = useState(false)
    let types=0
    types += item.type.map(type=> type.typeProducts.length)
    // const [favorites, setFavorites] = useState([])
    // const [meal, setMeal] = useState(item)
    // const {name} = useParams()
    //
    // useEffect( () => {
    //     async function getFavorites(){
    //     const response = await axios.get("http://localhost:3000/favorites")
    //     return setFavorites(response.data)}
    // }, []);
    // const clickHandler= async (item)=>{
    //     setMeal({...item, "isFavorite": !item.isFavorite, "logo": infoArr[0], "name":infoArr[1]})
    //     await axios.post("http://localhost:3000/favorites", item)
    //
    // }
    return <div className={'rounded-xl  border border-gray-100 cursor-pointer forShadow w-[250px] mb-4'}>
        {showModal && <Modal setShowModal={setShowModal} item={item} infoArr={infoArr}/>}

        <img src={item.image} alt={item.title} className={'rounded-t-xl h-[250px] object-cover'}
             onClick={()=>setShowModal(true)}/>
        <div className={'m-1 ml-2 mr-2'}>
            <div className={'font-semibold text-black/80 text-sm'}  onClick={()=>setShowModal(true)}>
                {item.title.length > 19 ? item.title.slice(0,19) +" ...": item.title}
            </div>
            {item.type? <div className={'text-xs text-black/60 mb-2'}>{types} տեսակ</div>:<div className={'h-6'}></div>}
            <div className={'flex justify-between '}>
                <span className={'font-semibold text-black/80'}  onClick={()=>setShowModal(true)}>{item.price}֏</span>
                <span>
                    <i className="fa-regular fa-heart text-red-600 mr-2.5 text-xl"></i>
                    <i className="fa-solid fa-cart-plus text-red-600 text-[18px]"></i>
                </span>
            </div>
        </div>
    </div>
}
