import {useNavigate} from "react-router-dom";
import {useContext, useEffect, useState} from "react";
import {Modal} from "../Components/Modal";
import AppContext from "../AppContext";


export function BasketItem({item}){
    const navigate = useNavigate()
    const [showModal, setShowModal] = useState(false)
    const {onFavorite, favorites, plus } = useContext(AppContext)
    const [isFavorite, setIsFavorite] = useState(false)
    useEffect(() => {
        favorites.map(el=>{
            if(el.id === item.id){
                setIsFavorite(true)
            }
        })
    }, []);

    const infoArr = [item.logo, item.name, item.categoryAPI, item.nameAPI]

    return <div className={'border-b-2 ml-2 mr-2 relative'}>
        {showModal && <Modal setShowModal={setShowModal} item={item} infoArr={infoArr} isFavorite={isFavorite} onFavorite={onFavorite} setIsFavorite={setIsFavorite}/>}
        <div className={'cursor-pointer text-black/80 font-semibold mb-2'}
             onClick={() => navigate(`/${item.categoryAPI}/${item.nameAPI}`)}>{item.name}
        </div>
        <div className={'mb-2 mr-2 flex cursor-pointer'} onClick={()=>setShowModal(true)}>
            <img src={item.image} alt={'nkar'} className={'w-[80px] h-[80px] rounded-md'}/>
            <div className={'w-full'}>
                <div className={'flex justify-between text-black/80 font-semibold  m-2'}>
                    <span>{item.title}</span>
                    <span>{item.price}÷è</span>
                </div>
                <div className={'text-xs m-2'}>121</div>
            </div>
        </div>

        <div className={'text-right absolute right-3 bottom-2'}>
            <button className={'bg-gray-200 rounded-md p-2 pt-0 pb-0'}>-</button>
            <span className={'ml-4 mr-4'}>{item.quantity}</span>
            <button className={'bg-gray-200 rounded-md p-2 pt-0 pb-0'} onClick={()=>plus(item.id)}>+</button>
        </div>

    </div>
}
